package Ejercicios;

public class OperacionesAritmeticas {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		System.out.println("El resultado de la operación " +(Math.pow(-5, 2)+10)*3);
		
		System.out.println("El resultado de la operación es " + (35.3-53)/10);
		
		System.out.println("El resultado de la operación es " + (1+(5%(1.5*8)/2)));
		
		System.out.println("El resultado de la operación es " + (-(Math.pow(-2, 5))+(18/5)));
		
		System.out.println("El resultado de la operación es " + ((65/3)/(4*3)));
		
		System.out.println("El resultado de la operación es " + ((44%10)+7-Math.pow(25, 10)-2));
		
		System.out.println("El resultado de la operacion es " + ((4/10)+(3.5*2)));
		
		System.out.println("El resultado de la operacion es " + ((10%4)+(6/2)));
		
		System.out.println("El resultado de la operacion es " + (Math.pow(Math.abs((4)-(20/3)), 3)));
		
		System.out.println("El resultado es " + (Math.sqrt((4.5-5.0)+(7*3))));
		
		System.out.println("El resultado es " + ((3*10/3)+(10%3)));
		
		System.out.println("El resultado es " + (Math.pow(3L, 3)));
		
		System.out.println("El resultado es " + ((36/4)+(5*3)));
	
		System.out.println("El resultado es " + ((28/7)*2));
		
		System.out.println("El resultado es " + ((13*3)%10));
		
		
		System.out.println("El resultado es " + ((Math.pow(Math.pow(2, 3), 3))));
		
		
		
		
		
		
		
		
		
		
		
	}

}
