package EjerciciosPracticosNoOficales;

public class Cronometro {

	public static void main(String[] args) throws InterruptedException {
		// TODO Auto-generated method stub

		
		int i=0;
		
		while(i<10){
			Thread.sleep(1000);
			i++;
		}
		
		
		System.out.println("finish");
		
		
		
		
		
		
	}

}
